package week1.day2;

public class ConvertNegativeNumberToPositiveNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	
	  int number = -34;
	  
	
	
	  if (number <= 0) {
      System.out.println (number + "is negative" );	
      
      number = number * -1 ;
	}
	  
	  
      //to make it positive 
	
	 System.out.println (number + "is positive" );
		
		    
	
	
	}

}


	


